(34, 35, 'تعمیرگاه مزدا سیتی', 116, '<h2>خدمات در تعمیرگاه مزدا سیتی</h2>\r\n\r\n<p>تعمیرگاه مزدا سیتی خدمات خود را برای خودروی مزدا به میبرد و شما در صورت داشتن مشکل برای این خودروی خود میتوانید به مزدا سیتی مراجعه نمایید.<br />\r\n-&nbsp;&nbsp; &nbsp;تعمیر تخصصی بدنه<br />\r\n-&nbsp;&nbsp; &nbsp;تعمیر گیربکس<br />\r\n-&nbsp;&nbsp; &nbsp;تعمیر جلوبندی<br />\r\n-&nbsp;&nbsp; &nbsp;تعمیر موتور<br />\r\n-&nbsp;&nbsp; &nbsp;تعمیر برق</p>\r\n\r\n<p>&nbsp;</p>', 'تعمیرگاه مزدا سیتی', NULL, 'بزرگراه شیخ فضل الله، به سمت غرب، بعد از پل یادگار، بلوار چوب تراش، جنب مترو طرشت', 'تهران', 'تهران', '09122300900', NULL, NULL, NULL, NULL, NULL, NULL, '{\"crop\":\"\\/upload\\/images\\/company\\/34.png\",\"small\":\"\\/upload\\/images\\/company\\/34-small.png\",\"medium\":\"\\/upload\\/images\\/company\\/34-medium.png\",\"large\":\"\\/upload\\/images\\/company\\/34-large.png\"}', '35.65,51.4', 181, 'تعمیرگاه مزدا سیتی | ریموت یدک', 'تعمیرگاه مزدا سیتی', 'تعمیرگاه مزدا سیتی خدمات خود را برای خودروی مزدا به میبرد و شما در صورت داشتن مشکل برای این خودروی خود میتوانید به مزدا سیتی مراجعه نمایید.', 1, '2021-08-20 16:50:28', '2023-02-20 10:17:24'),
(35, 37, 'تعمیرگاه پایتخت', 119, '<h2>خدمات تعمیرگاه پایتخت</h2>\r\n\r\n<p>بواسطه افراد مجرب درانواع&nbsp; زمینه های مکانیکی و کارشناسی خودرو بهترین خدمات را ارائه میدهد. خدمات شامل خودرو های انواع پژو ،&nbsp; هیوندای ، ریو ،کیا ، پورشه و... میشود.<br />\r\nانواع خدمات در پایتخت<br />\r\n-&nbsp;&nbsp; &nbsp;مکانیکی<br />\r\n-&nbsp;&nbsp; &nbsp;تنظیم موتور<br />\r\n-&nbsp;&nbsp; &nbsp;تعمیر دنده<br />\r\n-&nbsp;&nbsp; &nbsp;تعمیر گیربکس<br />\r\n-&nbsp;&nbsp; &nbsp;تعویض روغن<br />\r\n-&nbsp;&nbsp; &nbsp;کارشناسی بدنه<br />\r\n-&nbsp;&nbsp; &nbsp;جلوبندی<br />\r\n-&nbsp;&nbsp; &nbsp;دیاگ<br />\r\n-&nbsp;&nbsp; &nbsp;سرویس دوره ای<br />\r\n-&nbsp;&nbsp; &nbsp;شارژ کولر</p>\r\n\r\n<p>&nbsp;</p>', 'تعمیرگاه پایتخت', 'تعمیرگاه پایتخت', 'میدان قزوین ، خیابان قزوین بعد از نادر خیری ف جنب پل نواب پلاک 565', 'تهران', 'تهران', '123445', '\"021-55417070,021-55404949,021-55409020\"', 'paytakhtservice@gmal.com', 'paytakhtservice.ir', NULL, NULL, NULL, '{\"crop\":\"\\/upload\\/images\\/company\\/35.png\",\"small\":\"\\/upload\\/images\\/company\\/35-small.png\",\"medium\":\"\\/upload\\/images\\/company\\/35-medium.png\",\"large\":\"\\/upload\\/images\\/company\\/35-large.png\"}', '35.65,51.4', 243, 'تعمیرگاه پایتخت | ریموت یدک', 'تعمیرگاه پایتخت', 'خدمات در زمینه تعمیر پژو - پورشه - کیا - هیوندا ، ریو و ... - فروش مدل های مختلف خودرو صفر و کارکرده', 1, '2021-09-04 10:03:41', '2023-03-01 13:50:58'),
